import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/login.css';

function Register() {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [message, setMessage] = useState('');
  const [timerActive, setTimerActive] = useState(false);
  const [timeLeft, setTimeLeft] = useState(120);
  const [attempts, setAttempts] = useState(0);
  const [tooManyRequests, setTooManyRequests] = useState(false);
  const [cooldownTime, setCooldownTime] = useState(0);
  const [otpExpired, setOtpExpired] = useState(false);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    phone_number: '',
    password: '',
    confirmPassword: '',
    group_id: '',
  });

  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleOtpChange = (e) => setOtp(e.target.value);

  const handleSendOtp = async () => {
    if (attempts >= 3) {
      setTooManyRequests(true);
      setCooldownTime(60);
      setMessage('60 seconds');
      return;
    }

    try {
      const res = await fetch('http://localhost:3000/api/users/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await res.json();
      if (res.ok) {
        setOtpSent(true);
        setOtpExpired(false);
        setTimerActive(true);
        setTimeLeft(120);
        setAttempts((prev) => prev + 1);
        setMessage('OTP sent to your email.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
        if (data.message?.includes('Too many OTP requests')) {
          setTooManyRequests(true);
          const wait = data.waitTime || 60;
          setCooldownTime(wait);
          setMessage(`${wait} seconds`);
        }
      }
    } catch (error) {
      console.error('Error in handleSendOtp:', error.message);
      setMessage('Network error while sending OTP: ' + error.message);
    }
  };

  const handleVerifyOtp = async () => {
    try {
      const res = await fetch('http://localhost:3000/api/users/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp }),
      });
      const data = await res.json();
      if (res.ok) {
        setOtpVerified(true);
        setTimerActive(false);
        setMessage('Email verified! You can now complete registration.');
      } else {
        setMessage(data.message || 'Invalid OTP');
      }
    } catch (error) {
      console.error('Error in handleVerifyOtp:', error.message);
      setMessage('Network error during OTP verification: ' + error.message);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, phone_number, password, confirmPassword, group_id } = formData;

    if (!name || !phone_number || !password || !confirmPassword || group_id === '') {
      setMessage('Please fill all fields');
      return;
    }

    if (password !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }

    try {
      const payload = { ...formData, email };
      const res = await fetch('http://localhost:3000/api/users/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (res.ok) {
        alert('Registration successful! Please log in.');
        navigate('/');
      } else {
        setMessage(data.message || 'Registration failed');
      }
    } catch (error) {
      console.error('Error in handleSubmit:', error.message);
      setMessage('Network error during registration: ' + error.message);
    }
  };

  useEffect(() => {
    let interval;
    if (timerActive && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timerActive && timeLeft === 0) {
      setTimerActive(false);
      setOtpExpired(true);
      setOtp('');
      setMessage('OTP expired. Request a new OTP.');
    }
    return () => clearInterval(interval);
  }, [timerActive, timeLeft]);

  useEffect(() => {
    let cooldownInterval;
    if (tooManyRequests && cooldownTime > 0) {
      cooldownInterval = setInterval(() => {
        setCooldownTime((prevTime) => {
          const nextTime = prevTime - 1;
          if (nextTime === 0) {
            setTooManyRequests(false);
            setAttempts(0);
            setMessage('You can now request a new OTP.');
          } else {
            setMessage(`${nextTime} seconds`);
          }
          return nextTime;
        });
      }, 1000);
    }
    return () => clearInterval(cooldownInterval);
  }, [tooManyRequests, cooldownTime]);

  return (
    <div className="flex font-poppins items-center justify-center min-h-screen">
      <div className="w-full max-w-md p-5 space-y-5 bg-white rounded-2xl">
        <h1 className="text-4xl font-bold text-center text-gray-800">Register</h1>

        {!otpVerified ? (
          <>
            <div className="input-group">
              <label htmlFor="email" className="block text-md text-gray-700 mb-2">Email</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={handleEmailChange}
                required
                className="input-group input"
                placeholder="you@example.com"
                disabled={tooManyRequests}
              />
            </div>

            <button
              onClick={handleSendOtp}
              className="login-button"
              disabled={(timerActive && !otpExpired) || tooManyRequests}
            >
              Send OTP
            </button>

            {otpSent && (
              <>
                <div className="input-group">
                  <label htmlFor="otp" className="block text-md text-gray-700 mb-2">Enter OTP</label>
                  <input
                    id="otp"
                    type="text"
                    value={otp}
                    onChange={handleOtpChange}
                    required
                    className="input-group input"
                    placeholder="Enter OTP"
                    disabled={tooManyRequests || otpExpired}
                  />
                </div>
                <button
                  onClick={handleVerifyOtp}
                  className="login-button"
                  disabled={tooManyRequests || otpExpired}
                >
                  Verify OTP
                </button>
                {timerActive && !otpExpired && (
                  <div className="text-center mt-4">
                    <p>Time left to enter OTP: <span>{timeLeft}</span> seconds</p>
                  </div>
                )}
              </>
            )}

            <p className="text-center text-red-500 mt-2">{message}</p>
          </>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
            <h3 className="text-xl font-semibold text-center">Complete your registration</h3>

            <div className="input-group">
              <label htmlFor="name" className="block text-md text-gray-700 mb-2">Full Name</label>
              <input
                id="name"
                name="name"
                type="text"
                value={formData.name}
                onChange={handleChange}
                required
                className="input-group input"
              />
            </div>

            <div className="input-group">
              <label htmlFor="phone_number" className="block text-md text-gray-700 mb-2">Phone Number</label>
              <input
                id="phone_number"
                name="phone_number"
                type="text"
                value={formData.phone_number}
                onChange={handleChange}
                required
                className="input-group input"
              />
            </div>

            <div className="input-group">
              <label htmlFor="password" className="block text-md text-gray-700 mb-2">Password</label>
              <input
                id="password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                className="input-group input"
              />
            </div>

            <div className="input-group">
              <label htmlFor="confirmPassword" className="block text-md text-gray-700 mb-2">Confirm Password</label>
              <input
                id="confirmPassword"
                name="confirmPassword"
                type="password"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
                className="input-group input"
              />
            </div>

            <div className="input-group">
              <label htmlFor="group_id" className="block text-md text-gray-700 mb-2">Select Role</label>
              <select
                id="group_id"
                name="group_id"
                value={formData.group_id}
                onChange={handleChange}
                required
                className="input-group input"
              >
                <option value="" disabled hidden>Select Role</option>
                <option value="1">User</option>
                <option value="0">Admin</option>
              </select>
            </div>

            <button type="submit" className="login-button">Register</button>
            {message && <p className="text-red-500 text-center mt-2">{message}</p>}
          </form>
        )}
      </div>
    </div>
  );
}

export default Register;
