import React, { useState } from 'react';

import './css/login.css';

function Login() {
    const [isLogin, setIsLogin] = useState(true);
    const [showPassword, setShowPassword] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',  // ✅ Needed to send cookies
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();
            if (!response.ok) throw new Error(data.message);

            alert(`Welcome ${data.username}`);
        } catch (err) {
            alert('Login failed: ' + err.message);
        }
    };

    return (
        <div className="flex font-poppins items-center justify-center min-h-screen">
            <div className="w-full max-w-md p-5 space-y-5 bg-white rounded-2xl">
                <h1 className="text-4xl font-bold text-center text-gray-800 dark:text-gray-100">
                    {isLogin ? "Log in" : "Sign up"}
                </h1>

                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label htmlFor="email" className="block text-md text-gray-700 dark:text-gray-300 mb-2">
                            Email
                        </label>
                        <input
                            id="email"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            placeholder="Email"
                            className="w-full p-3 border rounded-xl bg-gray-100 dark:bg-indigo-700 dark:text-gray-100"
                        />
                    </div>

                    <div className="relative">
                        <label htmlFor="password" className="block text-md text-gray-700 dark:text-gray-300 mb-2">
                            Password
                        </label>
                        <input
                            id="password"
                            type={showPassword ? "text" : "password"}
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            placeholder="Password"
                            className="w-full p-3 pr-10 border rounded-xl bg-gray-100 dark:bg-indigo-700 dark:text-gray-100"
                        />
                        <span
                            className="absolute top-11 right-4 text-gray-500 cursor-pointer"
                            onClick={() => setShowPassword(!showPassword)}
                        >
                            <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                        </span>
                    </div>

                    <button
                        type="submit"
                        className="w-full py-3 bg-gradient-to-r from-purple-500 to-indigo-500 text-white font-semibold rounded-xl"
                    >
                        {isLogin ? "Log In" : "Sign Up"}
                    </button>
                </form>

                <div className="text-center">
                    <span className="text-sm text-gray-700 dark:text-gray-300">
                        {isLogin ? "Don't have an account?" : "Already have an account?"}{' '}
                        <button
                            type="button"
                            onClick={() => setIsLogin(!isLogin)}
                            className="text-purple-500 hover:underline"
                        >
                            {isLogin ? "Sign Up" : "Log In"}
                        </button>
                    </span>
                </div>
            </div>
        </div>
    );
}

export default Login;
