import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import './css/login.css'; // if you want to use existing styles


function Register() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone_number: '',
    password: '',
    group_id: 1, // Default to regular user
    otp: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  // Step 1: Send OTP
  const sendOTP = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/send-otp', {
        email: formData.email
      }, { withCredentials: true });
      setMessage(res.data.message);
      setStep(2);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error sending OTP');
    }
  };

  // Step 2: Verify OTP and Register
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/register', formData, {
        withCredentials: true
      });
      setMessage(res.data.message);
      if (res.data.message === 'Registration successful') {
        setTimeout(() => {
          window.location.href = '/login'; // Redirect to login page
        }, 1500);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      {message && <p>{message}</p>}

      {step === 1 && (
        <form onSubmit={sendOTP}>
          <label>Email:</label>
          <input type="email" name="email" required onChange={handleChange} />
          <button type="submit">Send OTP</button>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleRegister}>
          <label>OTP:</label>
          <input type="text" name="otp" required onChange={handleChange} />

          <label>Name:</label>
          <input type="text" name="name" required onChange={handleChange} />

          <label>Phone Number:</label>
          <input type="text" name="phone_number" required onChange={handleChange} />

          <label>Password:</label>
          <input type="password" name="password" required onChange={handleChange} />

          <label>Register As:</label>
          <select name="group_id" onChange={handleChange}>
            <option value={1}>User</option>
            <option value={0}>Admin</option>
          </select>

          <button type="submit">Register</button>
        </form>
      )}
    </div>
  );
}

export default Register;
