import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/login.css';

function Login() {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.message);

      alert(`Welcome, ${data.username}!`);
      navigate('/dashboard');
    } catch (err) {
      alert('Login failed: ' + err.message);
    }
  };

  return (
    <div className="flex font-poppins items-center justify-center min-h-screen">
      <div className="w-full max-w-md p-5 space-y-5 bg-white rounded-2xl">
        <h1 className="text-4xl font-bold text-center text-gray-800">
          Log In
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="input-group">
            <label htmlFor="email" className="block text-md text-gray-700 mb-2">
              Email
            </label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="you@example.com"
              className="input-group input"
            />
          </div>

          <div className="relative input-group">
            <label htmlFor="password" className="block text-md text-gray-700 mb-2">
              Password
            </label>
            <input
              id="password"
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="••••••••"
              className="input-group input"
            />
            <span
              className="absolute top-11 right-4 text-gray-500 cursor-pointer"
              onClick={() => setShowPassword(!showPassword)}
            >
              <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
            </span>
          </div>

          <button type="submit" className="login-button">
            Log In
          </button>
        </form>

        <p className="toggle-container">
          Don’t have an account?{' '}
          <Link to="/register" className="toggle-text">
            Register here
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
