// CustomerDashboard.jsx - Just remove the sidebar part, keep everything else
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { jwtDecode } from 'jwt-decode';
import './css/login.css';
import { FaEdit } from 'react-icons/fa';

function CustomerDashboard() {
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [editAddress, setEditAddress] = useState('');
    const [editCanBuy, setEditCanBuy] = useState(false);
    const [editCanSell, setEditCanSell] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            Swal.fire({
                icon: 'error',
                title: 'Unauthorized',
                text: 'Please log in to access the dashboard.',
                confirmButtonColor: '#3085d6',
            }).then(() => navigate('/'));
            return;
        }

        let decodedToken;
        try {
            decodedToken = jwtDecode(token);
        } catch (err) {
            localStorage.removeItem('token');
            Swal.fire({
                icon: 'error',
                title: 'Invalid Token',
                text: 'Your session is invalid. Please log in again.',
                confirmButtonColor: '#3085d6',
            }).then(() => navigate('/'));
            return;
        }

        const groupId = Number(decodedToken.group_id);
        if (groupId !== 1) {
            Swal.fire({
                icon: 'error',
                title: 'Unauthorized',
                text: 'Customers only (group_id = 1).',
                confirmButtonColor: '#3085d6',
            }).then(() => navigate('/'));
            return;
        }

        fetch('http://localhost:3000/api/customer/profile', {
            headers: { Authorization: `Bearer ${token}` },
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.message === 'Profile not found' || !data.photo) {
                    Swal.fire({
                        icon: 'warning',
                        title: 'Profile Incomplete',
                        text: !data.photo ? 'Please upload a profile photo to complete your profile.' : 'Please complete your profile.',
                        confirmButtonColor: '#3085d6',
                    }).then(() => navigate('/complete-profile'));
                } else {
                    setProfile(data);
                    setEditAddress(data.address || '');
                    setEditCanBuy(data.can_buy || false);
                    setEditCanSell(data.can_sell || false);
                    setLoading(false);
                }
            })
            .catch((err) => {
                console.error('Error fetching profile:', err);
                setError('Failed to load profile.');
                setLoading(false);
            });
    }, [navigate]);

    const handleEditProfile = () => {
        setIsEditing(true);
    };

    const handleSaveProfile = () => {
        if (!editAddress.trim()) {
            Swal.fire({
                icon: 'warning',
                title: 'Address Required',
                text: 'Please provide a valid address.',
                confirmButtonColor: '#3085d6',
            });
            return;
        }

        const token = localStorage.getItem('token');
        const formData = new FormData();
        formData.append('address', editAddress);
        formData.append('can_buy', editCanBuy.toString());
        formData.append('can_sell', editCanSell.toString());

        fetch('http://localhost:3000/api/customer/profile', {
            method: 'PUT',
            headers: {
                Authorization: `Bearer ${token}`,
            },
            body: formData,
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    setProfile({ ...profile, address: editAddress, can_buy: editCanBuy, can_sell: editCanSell });
                    setIsEditing(false);
                    Swal.fire({
                        icon: 'success',
                        title: 'Profile Updated',
                        text: 'Your profile has been updated successfully.',
                        confirmButtonColor: '#3085d6',
                    });
                } else {
                    Swal.fire({
                        icon: 'error',
                        title: 'Update Failed',
                        text: data.message || 'Failed to update profile. Please try again.',
                        confirmButtonColor: '#3085d6',
                    });
                }
            })
            .catch((err) => {
                console.error('Error updating profile:', err);
                Swal.fire({
                    icon: 'error',
                    title: 'Update Failed',
                    text: 'An error occurred while updating your profile.',
                    confirmButtonColor: '#3085d6',
                });
            });
    };

    const handleCancelEdit = () => {
        setEditAddress(profile.address || '');
        setEditCanBuy(profile.can_buy || false);
        setEditCanSell(profile.can_sell || false);
        setIsEditing(false);
    };

    if (loading) return <div className="text-center p-10 text-gray-600">Loading...</div>;
    if (error) return <div className="text-center p-10 text-red-500">{error}</div>;

    let canSellDisplay;
    if (profile.can_sell) {
        canSellDisplay = 'Approved';
    } else if (profile.can_sell_status === 'pending') {
        canSellDisplay = 'Pending Approval';
    } else if (profile.can_sell_status === 'rejected') {
        canSellDisplay = 'Rejected';
    } else {
        canSellDisplay = 'Not Requested';
    }

    return (
        // REMOVED THE SIDEBAR DIV - Only keeping the main content
        <div className="p-8">
            <div className="flex items-center justify-between mb-8">
                <h2 className="text-4xl font-bold text-gray-800">Customer Dashboard</h2>
                <div className="flex items-center space-x-4">
                    <button
                        onClick={handleEditProfile}
                        className="flex items-center px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200"
                    >
                        <FaEdit className="mr-2" />
                        Edit Profile
                    </button>
                    {profile?.photo && (
                        <img
                            src={`http://localhost:3000${profile.photo}`}
                            alt="Profile"
                            className="w-16 h-16 object-cover rounded-full border-2 border-blue-500 shadow-md"
                            onError={(e) => {
                                console.error('Failed to load image:', e);
                                e.target.style.display = 'none';
                            }}
                        />
                    )}
                </div>
            </div>

            <div className="mb-6">
                <h3 className="text-2xl font-semibold text-gray-700">
                    Welcome, {profile?.username || 'User'}!
                </h3>
                <p className="text-gray-500">Manage your goats and explore the Digi Goat platform.</p>
            </div>

            <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Profile Overview</h3>
                {isEditing ? (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-gray-700 font-medium">Address:</label>
                            <input
                                type="text"
                                value={editAddress}
                                onChange={(e) => setEditAddress(e.target.value)}
                                className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        <div className="flex items-center">
                            <label className="text-gray-700 font-medium mr-2">Can Buy:</label>
                            <input
                                type="checkbox"
                                checked={editCanBuy}
                                onChange={(e) => setEditCanBuy(e.target.checked)}
                                className="h-5 w-5 text-blue-500"
                            />
                        </div>
                        <div className="flex items-center">
                            <label className="text-gray-700 font-medium mr-2">Request to Sell:</label>
                            {profile.can_sell ? (
                                <span className="text-gray-700">Approved</span>
                            ) : profile.can_sell_status === 'pending' ? (
                                <span className="text-gray-700">Pending Approval</span>
                            ) : profile.can_sell_status === 'rejected' ? (
                                <span className="text-gray-700">Rejected</span>
                            ) : (
                                <input
                                    type="checkbox"
                                    checked={editCanSell}
                                    onChange={(e) => setEditCanSell(e.target.checked)}
                                    className="h-5 w-5 text-blue-500"
                                />
                            )}
                        </div>
                        <div className="flex space-x-4">
                            <button
                                onClick={handleSaveProfile}
                                disabled={!editAddress.trim()}
                                className={`px-4 py-2 rounded-lg transition-colors duration-200 ${editAddress.trim()
                                    ? 'bg-blue-500 text-white hover:bg-blue-600'
                                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                    }`}
                            >
                                Save
                            </button>
                            <button
                                onClick={handleCancelEdit}
                                className="bg-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-400 transition-colors duration-200"
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                ) : (
                    <div className="space-y-3">
                        <p className="text-gray-700">
                            <strong className="font-medium">Name:</strong> {profile?.username || 'N/A'}
                        </p>
                        <p className="text-gray-700">
                            <strong className="font-medium">Email:</strong> {profile?.email || 'N/A'}
                        </p>
                        <p className="text-gray-700">
                            <strong className="font-medium">Address:</strong> {profile?.address || 'Not set'}
                        </p>
                        <p className="text-gray-700">
                            <strong className="font-medium">Can Buy:</strong> {profile?.can_buy ? 'Yes' : 'No'}
                        </p>
                        <p className="text-gray-700">
                            <strong className="font-medium">Can Sell:</strong> {canSellDisplay}
                        </p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default CustomerDashboard;
